const createTransferMutation = {
  query: `
    mutation {
      createTransfer(from: "julio", to: "isabelle", value: 100) {
        from
        to
        value
      }
    }
  `
};
const res = await request(app)
  .post('/graphql')
  .set('Authorization', `Bearer ${token}`)
  .send(createTransferMutation);
